{
    "title": "Python Quiz",
    "description": "A quiz to test your knowledge of Python programming concepts",
    "questions": [
        {
            "questionText": "Which of the following is a valid variable name in Python?",
            "options": [
                "2var",
                "var_name",
                "var-name",
                "var name"
            ],
            "correctAnswer": "var_name"
        },
        {
            "questionText": "What does the 'len()' function do in Python?",
            "options": [
                "Calculates the length of a string or collection",
                "Returns the last element of a list",
                "Counts occurrences of a character",
                "None of the above"
            ],
            "correctAnswer": "Calculates the length of a string or collection"
        },
        {
            "questionText": "What is the output of: print(type(5))?",
            "options": [
                "<class 'int'>",
                "<type 'int'>",
                "int",
                "Error"
            ],
            "correctAnswer": "<class 'int'>"
        },
        {
            "questionText": "Which Python keyword is used to define a function?",
            "options": [
                "func",
                "function",
                "def",
                "lambda"
            ],
            "correctAnswer": "def"
        },
        {
            "questionText": "What is the output of: print(2 ** 3)?",
            "options": [
                "6",
                "8",
                "9",
                "Error"
            ],
            "correctAnswer": "8"
        },
        {
            "questionText": "Which of the following is used to handle exceptions in Python?",
            "options": [
                "try-catch",
                "try-except",
                "if-else",
                "while-try"
            ],
            "correctAnswer": "try-except"
        },
        {
            "questionText": "What does the 'is' operator check in Python?",
            "options": [
                "Equality of values",
                "Identity of objects",
                "Membership in a collection",
                "None of the above"
            ],
            "correctAnswer": "Identity of objects"
        },
        {
            "questionText": "What does 'pip' stand for in Python?",
            "options": [
                "Python Installation Package",
                "Pip Installs Python",
                "Pip Installs Packages",
                "None of the above"
            ],
            "correctAnswer": "Pip Installs Packages"
        },
        {
            "questionText": "What is the correct file extension for Python files?",
            "options": [
                ".python",
                ".py",
                ".pyt",
                ".pt"
            ],
            "correctAnswer": ".py"
        },
        {
            "questionText": "Which data type is immutable in Python?",
            "options": [
                "List",
                "Set",
                "Dictionary",
                "Tuple"
            ],
            "correctAnswer": "Tuple"
        },
        {
            "questionText": "What is the output of: print(bool(''))?",
            "options": [
                "True",
                "False",
                "Error",
                "None"
            ],
            "correctAnswer": "False"
        },
        {
            "questionText": "How do you define a dictionary in Python?",
            "options": [
                "{}",
                "[]",
                "()",
                "None"
            ],
            "correctAnswer": "{}"
        },
        {
            "questionText": "Which Python keyword is used to create a generator?",
            "options": [
                "yield",
                "return",
                "generator",
                "lambda"
            ],
            "correctAnswer": "yield"
        },
        {
            "questionText": "What is the output of: print(10 // 3)?",
            "options": [
                "3",
                "3.33",
                "3.0",
                "4"
            ],
            "correctAnswer": "3"
        },
        {
            "questionText": "What does the 'break' statement do?",
            "options": [
                "Exits the loop",
                "Restarts the loop",
                "Skips to the next iteration",
                "Ends the program"
            ],
            "correctAnswer": "Exits the loop"
        },
        {
            "questionText": "What is a correct way to create a class in Python?",
            "options": [
                "class MyClass()",
                "def MyClass()",
                "function MyClass()",
                "object MyClass()"
            ],
            "correctAnswer": "class MyClass()"
        },
        {
            "questionText": "Which Python library is used for data analysis?",
            "options": [
                "NumPy",
                "Pandas",
                "Matplotlib",
                "Scikit-learn"
            ],
            "correctAnswer": "Pandas"
        },
        {
            "questionText": "What is the output of: print(type([1, 2, 3]))?",
            "options": [
                "<class 'list'>",
                "<type 'list'>",
                "list",
                "Error"
            ],
            "correctAnswer": "<class 'list'>"
        },
        {
            "questionText": "How do you import a module in Python?",
            "options": [
                "module import",
                "import module",
                "import()",
                "load module"
            ],
            "correctAnswer": "import module"
        },
        {
            "questionText": "Which of the following is used to define a block of code in Python?",
            "options": [
                "{}",
                "()",
                "Indentation",
                "[]"
            ],
            "correctAnswer": "Indentation"
        }
    ]
}
